def floor_series_with_na_math(series):
  """
  NaNを含むdouble型のSeriesをmath.floorで小数点切り捨てする

  Args:
    series (pd.Series): 対象のSeries

  Returns:
    pd.Series: 小数点切り捨て後のSeries
  """
  return series.apply(lambda x: math.floor(x) if pd.notna(x) else x)

